use globalmart;
GO
if not EXISTS (select * from sys.external_file_formats where name='csv_file_format')
CREATE EXTERNAL FILE FORMAT csv_file_format
with(
    FORMAT_TYPE=DELIMITEDTEXT,
    FORMAT_OPTIONS(
        FIELD_TERMINATOR=',',
        STRING_DELIMITER='"',
        FIRST_ROW=2,
        USE_TYPE_DEFAULT=FALSE,
        ENCODING='UTF8',
        PARSER_VERSION='2.0')
    );
if not EXISTS (select * from sys.external_file_formats where name='json_file_format')
CREATE EXTERNAL FILE FORMAT json_file_format
WITH (
    FORMAT_TYPE = JSON
);
